import { useEffect, useState } from "react"
import PaypalExpressBtn from 'react-paypal-express-checkout';
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";

export default function PaymentMethod(props) {
    let currency = useSelector(state => state.config.currency);
    const orderId = useSelector(state => state.config.order ? state.config.order : null);
    const [paymentMethod, setPaymentMethod] = useState(null);
    const amountToPay = parseFloat((props.amount * currency.value).toFixed(2));

    useEffect(() => {
        if (paymentMethod === "bank" && document.getElementById("cardForm")) {
            document.getElementById("cardForm").scrollIntoView({
                behavior: "smooth"
            })
        }
    }, [paymentMethod]);

    const onSuccess = (payment) => {
        //console.log(payment)
    }

    const onError = (err) => {
        //console.log(err)
    }

    const onCancel = (data) => {
        //console.log(data)
    }

    return (
        <div className="dis_detail blockBorder" id="paymentMethods">
            <div>
                <h4 className="p0">Payment Method</h4>
            </div>
            <hr className="seperator" />
            <div className="center-block">
                <div className="row">
                    <div className="col-md-12 col-12">
                        <div className="paymentMethod" onClick={() => setPaymentMethod("bank")}>
                            <span className="radio-item">
                                <input type="radio" name="ritem" onChange={() => setPaymentMethod("bank")} value="bank" checked={(paymentMethod === "bank")} />
                                <label>Credit or debit card</label>
                            </span>
                        </div>
                        {
                            <div className="payViaBank">
                                
                            </div>
                        }
                        <div className="paymentMethod" onClick={() => setPaymentMethod("paypal")}>
                            <span className="radio-item">
                                <input type="radio" name="paymentMethod" onChange={() => setPaymentMethod("paypal")} value="paypal" checked={(paymentMethod === "paypal")} />
                                <label>Paypal express checkout</label>
                            </span>
                        </div>
                        {
                            (paymentMethod === "paypal") && (<PaypalExpressBtn client={{
                                sandbox: "AVqcfq3E-ODuZ1IfzPWB6famglBPiyHOBxVV0WTsIkkf_fDOM84Im006OuyiqATkr_O88kJIQeZWtP-6"
                            }} currency={currency.code} total={amountToPay} style={{
                                size: 'responsive',
                                color: 'gold',
                                shape: 'pill',
                                label: 'pay',
                                tagline: true,
                                fundingicons: true
                            }} onCancel={onCancel} onError={onError} onSuccess={onSuccess} />)
                        }
                    </div>
                </div>
            </div>
        </div>
    )
}
