import { useEffect, useState } from "react"
import PaypalExpressBtn from 'react-paypal-express-checkout';
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";

export default function PaymentMethod(props) {
    let currency = useSelector(state => state.config.currency);
    const orderId = useSelector(state => state.config.order ? state.config.order : null);
    const [paymentMethod, setPaymentMethod] = useState(null);
    const amountToPay = parseFloat((props.amount * currency.value).toFixed(2));

    useEffect(() => {
        if (paymentMethod === "bank" && document.getElementById("cardForm")) {
            document.getElementById("cardForm").scrollIntoView({
                behavior: "smooth"
            })
        }
    }, [paymentMethod]);

    const onSuccess = (payment) => {
        //console.log(payment)
    }

    const onError = (err) => {
        //console.log(err)
    }

    const onCancel = (data) => {
        //console.log(data)
    }

    return (
        <div className="dis_detail blockBorder" id="paymentMethods">
            <div>
                <h4 className="p0">Payment Method</h4>
            </div>
            <hr className="seperator" />
            <div className="center-block">
                <div className="row">
                    <div className="col-md-12 col-12">
                        <div className="paymentMethod" onClick={() => setPaymentMethod("bank")}>
                            <span className="radio-item">
                                <input type="radio" name="ritem" onChange={() => setPaymentMethod("bank")} value="bank" checked={(paymentMethod === "bank")} />
                                <label>Credit or debit card</label>
                            </span>
                        </div>
                        {
                            <div className="payViaBank">
                                <p>You'll be redirected to payment page, where you can pay via credit/debit card</p>
                                <form name="payFormCcard" method="post" action="https://ipay.bangkokbank.com/b2c/eng/payment/payForm.jsp">
                                    <input type="hidden" name="merchantId" value="6848" />
                                    <input type="hidden" name="amount" value="3000.0" />
                                    <input type="hidden" name="orderRef" value="000000000014" />
                                    <input type="hidden" name="currCode" value="344" />
                                    <input type="hidden" name="successUrl" value="http://www.yourdomain.com/Success.html" />
                                    <input type="hidden" name="failUrl" value="http://www.yourdomain.com/Fail.html" />
                                    <input type="hidden" name="cancelUrl" value="http://www.yourdomain.com/Cancel.html" />
                                    <input type="hidden" name="payType" value="N" />
                                    <input type="hidden" name="lang" value="E" />
                                    <input type="hidden" name="remark" value="-" />
                                    <input type="hidden" name="redirect" value="30" />
                                    <input type="hidden" name="orderRef1" value="add-ref-00001" />
                                    <input type="hidden" name="orderRef2" value="add-ref-00002" />
                                    <input type="hidden" name="orderRef3" value="add-ref-00003" />
                                    <input type="hidden" name="orderRef4" value="add-ref-00004" />
                                    <input type="hidden" name="orderRef5" value="add-ref-00005" />
                                    <input type="submit" />
                                </form>
                            </div>
                        }
                        <div className="paymentMethod" onClick={() => setPaymentMethod("paypal")}>
                            <span className="radio-item">
                                <input type="radio" name="paymentMethod" onChange={() => setPaymentMethod("paypal")} value="paypal" checked={(paymentMethod === "paypal")} />
                                <label>Paypal express checkout</label>
                            </span>
                        </div>
                        {
                            (paymentMethod === "paypal") && (<PaypalExpressBtn client={{
                                sandbox: "AVqcfq3E-ODuZ1IfzPWB6famglBPiyHOBxVV0WTsIkkf_fDOM84Im006OuyiqATkr_O88kJIQeZWtP-6"
                            }} currency={currency.code} total={amountToPay} style={{
                                size: 'responsive',
                                color: 'gold',
                                shape: 'pill',
                                label: 'pay',
                                tagline: true,
                                fundingicons: true
                            }} onCancel={onCancel} onError={onError} onSuccess={onSuccess} />)
                        }
                    </div>
                </div>
            </div>
        </div>
    )
}
