import axios from "axios";
import { useEffect, useState } from "react";

export default function AddressForm() {
    const [countries, setCountries] = useState([]);
    const [zones, setZones] = useState([]);
    const [isSaving, setIsSaving] = useState(false);
    const [addresses, setAddresses] = useState([]);

    useEffect(() => {
        axios.get(`${process.env.API_URL}static/countries`).then(res => {
            setCountries(res.data);
            let zones = []

            res.data.forEach(country => {
                country.zones.forEach(zone => {
                    zones.push({ id: zone.id, name: zone.name })
                })
            })
            setZones(zones)
        })
    }, [])

    const fetchZones = (e) => {
        let countryData = countries.filter(c => c.id == e.target.value);
        let zones = []
        countryData.forEach(country => {
            country.zones.forEach(zone => {
                zones.push({ id: zone.id, name: zone.name })
            })
        })

        setZones(zones)
    }

    const saveAddress = (e) => {
        e.preventDefault()
        let request = {
            name: e.target.name.value,
            phone: e.target.phone.value,
            address: e.target.address.value,
            postcode: e.target.postcode.value,
            countryId: e.target.countryId.value,
            zoneId: e.target.zoneId.value,
            type: e.target.type.value,
            isDefault: e.target.isDefault.value,
        }

        setIsSaving(true)
        axios.post(`${process.env.API_URL}address`, request).then(res => {
            setAddNew(false)
            setIsSaving(false)
        }).catch(err => {
            setIsSaving(false)
        })
    }

    return (
        <div className="form_aria_p">
            <form onSubmit={saveAddress}>
                <div className="row">
                    <div className="col-md-6">
                        <div className="floating-label">
                            <input className="floating-input" name="name" type="text" placeholder=" " required="required" />
                            <span className="highlight"></span>
                            <label>First Name</label>
                        </div>
                    </div>
                    <div className="col-md-6">
                        <div className="floating-label">
                            <input className="floating-input" name="phone" type="text" placeholder=" " required="required" />
                            <span className="highlight"></span>
                            <label>Phone Number</label>
                        </div>
                    </div>
                    <div className="col-md-12">
                        <div className="floating-label">
                            <input className="floating-input" name="address" type="text" placeholder=" " required="required" />
                            <span className="highlight"></span>
                            <label>Complete Address</label>
                        </div>
                    </div>
                    <div className="col-md-6">
                        <div className="floating-label">
                            <input className="floating-input" name="postcode" type="text" placeholder=" " />
                            <span className="highlight"></span>
                            <label>Postcode</label>
                        </div>
                    </div>
                    <div className="col-md-6">
                        <div className="floating-label">
                            <select className="floating-select" onChange={fetchZones} name="countryId" defaultValue={1}>
                                {
                                    countries.map(c => {
                                        return (<option value={c.id} key={c.id}>{c.name}</option>)
                                    })
                                }
                            </select>
                            <span className="highlight"></span>
                            <label>Country</label>
                        </div>
                    </div>
                    <div className="col-md-6">
                        <div className="floating-label">
                            <select className="floating-select" name="zoneId" defaultValue={1}>
                                {
                                    zones.map(z => {
                                        return (<option value={z.id} key={z.id}>{z.name}</option>)
                                    })
                                }
                            </select>
                            <span className="highlight"></span>
                            <label>State</label>
                        </div>
                    </div>
                    <div className="col-md-6">
                        <div className="floating-label">
                            <select className="floating-select" name="type" defaultValue="">
                                <option value="home">Home</option>
                                <option value="office">Office</option>
                            </select>
                            <span className="highlight"></span>
                            <label>Address Type</label>
                        </div>
                    </div>
                    <div className="col-md-6">
                        <div className="floating-label">
                            <select className="floating-select" name="isDefault" defaultValue="0">
                                <option value="0">No</option>
                                <option value="1">Yes</option>
                            </select>
                            <span className="highlight"></span>
                            <label>Is Default Address</label>
                        </div>
                    </div>
                    <div className="col-md-12">
                        <div className="btn-group btn-group-lg">
                            <button type="submit" className="bag_bttn">Save</button>
                            <button type="button" className="bag_bttn" onClick={() => setAddNew(false)} style={{ marginLeft: 2 }}>Cancel</button>
                        </div>
                    </div>
                </div>
            </form>
        </div>
    );
}
