import { combineReducers, createStore, applyMiddleware } from 'redux';
import { createWrapper, HYDRATE } from 'next-redux-wrapper';
import thunkMiddleware from "redux-thunk";
import axios from "axios";

let initState = {
    currency: {
        code: "THB",
        id: 4,
        status: 1,
        title: "THB",
        value: 1
    }
}

const reducer = async (state = initState, action) => {
    switch (action.type) {
        case HYDRATE:
            let currencies = await axios.get(`${process.env.API_URL}static/config`)
            currencies = currencies.data;
            console.log(currencies);
            let nextState = { ...state }
            return nextState
        case 'SET_CURRENCY':
            let nextStateup = { ...state, ...{ currency: action.payload } }
            return nextStateup
        case 'ADD_TO_CART':
            let cartAfterAdd = { ...state, ...{ cart: action.payload } }
            return cartAfterAdd
        case 'REMOVE_FROM_CART':
            let cartAfterRemove = { ...state, ...{ cart: action.payload } }
            return cartAfterRemove
        default:
            return state;
    }
};

const combinedReducer = combineReducers({
    config: reducer
})


// BINDING MIDDLEWARE
const bindMiddleware = (middleware) => {
    if (process.env.NODE_ENV !== "production") {
        const { composeWithDevTools } = require("redux-devtools-extension");
        return composeWithDevTools(applyMiddleware(...middleware));
    }
    return applyMiddleware(...middleware);
};

const makeStore = ({ isServer }) => {
    if (isServer) {
        //If it's on server side, create a store
        return createStore(combinedReducer, bindMiddleware([thunkMiddleware]));
    } else {
        //If it's on client side, create a store which will persist
        const { persistStore, persistReducer } = require("redux-persist");
        const storage = require("redux-persist/lib/storage").default;

        const persistConfig = {
            key: "gandhiStore",
            whitelist: ["config"], // only counter will be persisted, add other reducers if needed
            storage, // if needed, use a safer storage
        };

        const persistedReducer = persistReducer(persistConfig, combinedReducer); // Create a new reducer with our existing reducer

        const store = createStore(
            persistedReducer,
            bindMiddleware([thunkMiddleware])
        ); // Creating the store again

        store.__persistor = persistStore(store); // This creates a persistor object & push that persisted object to .__persistor, so that we can avail the persistability feature

        return store;
    }
};

// export an assembled wrapper
export const wrapper = createWrapper(makeStore);
