import { Fragment, useState } from "react";
import { toast, ToastContainer } from 'react-nextjs-toast'
import { useSelector, useDispatch } from "react-redux"

export default function CartButton(props) {
    const product = props.product
    const [qty, setQty] = useState(1)
    const [minQty] = useState(product.minOrderQuantity ? product.minOrderQuantity : 1)
    const [maxQty] = useState(product.maxOrderQuantity ? product.maxOrderQuantity : 200)
    const [step] = useState(product.step ? product.step : 1)

    let config = useSelector(state => state.config);
    const dispatch = useDispatch()

    const setQuantity = (val) => {

        /* if (val < minQty) {
            toast.notify(`Invalid quantity, min order quantity is : ${minQty}`, {
                type: "error",
                title: "Quantity Error!!!"
            })

            return;
        }

        if (val > maxQty) {
            toast.notify(`Invalid quantity, max order quantity is : ${maxQty}`, {
                type: "error",
                title: "Quantity Error!!!"
            })

            return;
        } */

        val = parseFloat(val)
        setQty(parseFloat(val.toFixed(2)))
    }

    const addProductToCart = () => {
        console.log(config)
        if (qty < minQty) {
            toast.notify(`Invalid quantity, min order quantity is : ${minQty}`, {
                type: "error",
                title: "Error!!!"
            })
        } else if (qty > maxQty) {
            toast.notify(`Invalid quantity, max order quantity is : ${maxQty}`, {
                type: "error",
                title: "Error!!!"
            })
        } else {
            let item = { id: product.id, quantity: qty }
            let cartData = []
            if (config.cart) {
                cartData = config.cart.filter(ci => ci.id !== product.id)
            }

            cartData.push(item)

            dispatch({ type: "ADD_TO_CART", payload: cartData })
            toast.notify(`Added to cart`, {
                type: "success",
                title: "Success!!!"
            })
        }
    }

    return (
        <Fragment>
            <ToastContainer />
            {
                product.stockStatus ? (<div className="number-input md-number-input">
                    <p>Quantity</p>
                    <button className="minus" onClick={() => setQuantity(qty - step)}></button>
                    <input key={product.id} className="quantity" min="1" value={qty} defaultValue={qty} onChange={(e) => setQuantity(e.target.value)} name="quantity" type="number" />
                    <button className="plus" onClick={() => setQuantity(qty + step)}></button>
                </div>) : (
                        <div className="number-input md-number-input">
                            <h2>Out of Stock</h2>
                        </div>
                    )
            }
            {
                product.stockStatus !== 0 &&
                <Fragment>
                    <button type="button" className="bag_bttn" onClick={addProductToCart}>Add to Bag</button>
                    <button className="hard_icon"><i className="fa fa-heart" aria-hidden="true"></i></button>
                </Fragment>
            }
        </Fragment>
    )
}
