import { combineReducers, createStore } from 'redux';
import { createWrapper, HYDRATE } from 'next-redux-wrapper';

// create your reducer
const reducer = (state = {
    code: "THB",
    id: 4,
    status: 1,
    title: "THB",
    value: 1
}, action) => {
    switch (action.type) {
        case HYDRATE:
            let nextState = { currency: state }
            return nextState
        case 'SET_CURRENCY':
            let nextStateup = { ...state, ...{ currency: action.payload } }
            console.log(nextStateup);
            return nextStateup
        default:
            return state;
    }
};

// create a makeStore function
export const makeStore = context => createStore(combineReducers({
    config: reducer
}));

// export an assembled wrapper
