import { Fragment } from "react";
import { useSelector } from "react-redux";


export function getProductPrice(product) {
    const config = useSelector(state => state);

    if (product.salePrice !== 0) {
        return parseFloat((product.salePrice * config.currency.value).toFixed(2))
    }

    return parseFloat((product.ragularPrice * config.currency.value).toFixed(2))
}

export function formatPrice(price) {
    const storeConfig = useSelector(state => state.config);

    price = parseFloat((price * storeConfig.currency.value).toFixed(2))
    return new Intl.NumberFormat('en-IN', { style: "currency", currency: storeConfig.currency.code }).format(price)
}

export function GetPriceHtml(props) {
    if (props.product.salePrice === 0) {
        return (formatPrice(props.product.ragularPrice))
    }
    return (
        <Fragment>
            {formatPrice(props.product.salePrice) + "/Mtr "} <span><del>{formatPrice(props.product.ragularPrice)}/Mtr</del></span>
        </Fragment>
    )
}

export function setCookie(cname, cvalue, exdays) {
    var d = new Date();
    d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
    var expires = "expires=" + d.toUTCString();
    document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
}

export function getCookie(cname, str) {
    var name = cname + "=";
    var decodedCookie = decodeURIComponent(document.cookie);
    var ca = decodedCookie.split(';');
    for (var i = 0; i < ca.length; i++) {
        var c = ca[i];
        while (c.charAt(0) == ' ') {
            c = c.substring(1);
        }
        if (c.indexOf(name) == 0) {
            return c.substring(name.length, c.length);
        }
    }
    return "";
}

export function setCurrency(context) {
    let currency = getCookie(context.req.headers.cookie)
    console.log(currency)
}
