import { useSelector } from "react-redux";


export function getProductPrice(product) {
    const config = useSelector(state => state);

    if (product.salePrice !== 0) {
        return parseFloat((product.salePrice * config.currency.value).toFixed(2))
    }

    return parseFloat((product.ragularPrice * config.currency.value).toFixed(2))
}

export function formatPrice(price) {
    const storeConfig = useSelector(state => state);
    console.log(storeConfig);
    price = parseFloat((price * storeConfig.currency.value).toFixed(2))
    return new Intl.NumberFormat('en-IN', { style: "currency", currency: storeConfig.currency.code }).format(price)
}

export function getPriceHtml(product) {
    if (product.salePrice === 0) {
        return formatPrice(product.ragularPrice)
    }

    return `${formatPrice(product.salePrice)} <del>${formatPrice(product.ragularPrice)}</del>`
}
