import { useEffect, useState } from "react";
import axios from "axios";
import ProductCrousel from "../productCrousel"
import { toast, ToastContainer } from 'react-nextjs-toast';
import OwlCarousel from 'react-owl-carousel2';

export default function Wishlist() {
    const [wishlist, setWislist] = useState([]);
    const [reload, setReload] = useState(1);

    useEffect(() => {
        axios.get(`${process.env.API_URL}wishlist`).then(res => {
            setWislist(res.data.rows.map(wl => wl.product));
        })
    }, [reload])

    const removeFromWishliat = (productId) => {
        axios.post(`${process.env.API_URL}wishlist`, { productId: productId }).then(res => {
            toast.notify(`${res.data.message}`, {
                type: "success",
                title: "Wishlist!"
            })
            setReload(reload + 1)
        }).catch(err => {
            toast.notify(`Something went wrong!`, {
                type: "error",
                title: "Wishlist!"
            })
            setReload(reload + 1)
        })
    }

    return (
        <div className="tabcontent" style={{ display: 'block', left: '0px' }}>
            <ToastContainer />
            <h3>Wishlist</h3>
            <div className="row">
                <div className="col-md-12">
                    <OwlCarousel options={{
                        items: 2,
                        nav: true,
                        rewind: true,
                        autoplay: true
                    }} >
                        <ProductCrousel wishlist products={wishlist} removeFromWishliat={removeFromWishliat} />
                    </OwlCarousel>

                    <div className="mob_view">
                        <div className="wish_img">
                            <img src="images/reco_img_1.png" alt="" />
                            <div className="arrivals">
                                <a href="#">
                                    <p>Linen Chambray-4213219 <strong>₹770.00 / Mtr</strong></p>
                                </a>
                            </div>
                            <div className="f_icon"><i className="fa fa-heart" aria-hidden="true"></i></div>
                        </div>
                        <div className="wish_img">
                            <img src="images/reco_img_1.png" alt="" />
                            <div className="arrivals">
                                <a href="#">
                                    <p>Linen Chambray-4213219 <strong>₹770.00 / Mtr</strong></p>
                                </a>
                            </div>
                            <div className="f_icon"><i className="fa fa-heart" aria-hidden="true"></i></div>
                        </div>
                        <div className="wish_img">
                            <img src="images/reco_img_1.png" alt="" />
                            <div className="arrivals">
                                <a href="#">
                                    <p>Linen Chambray-4213219 <strong>₹770.00 / Mtr</strong></p>
                                </a>
                            </div>
                            <div className="f_icon"><i className="fa fa-heart" aria-hidden="true"></i></div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
